import React, { useState } from 'react';
import './App.css';

function App() {
  const [userInput, setUserInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false); // New state to handle loading
  const [error, setError] = useState(''); // New state to handle errors

  const sendMessage = async () => {
    if (!userInput) return;

    // Add the user message to the chat
    setMessages(prevMessages => [
      ...prevMessages,
      { text: userInput, isBot: false },
    ]);
    setLoading(true); // Set loading to true while waiting for the response
    setError(''); // Clear any previous error

    try {
      // Send the message using GET request as a query parameter
      const response = await fetch(`http://127.0.0.1:8000/ai/res/?question=${encodeURIComponent(userInput)}`, {
        method: 'GET', // Using GET method
      });

      if (!response.ok) {
        throw new Error('Server error, please try again later.');
      }

      const data = await response.json();

      // Add the bot's response to the chat
      setMessages(prevMessages => [
        ...prevMessages,
        { text: data.response, isBot: true },
      ]);
      setUserInput(''); // Clear input field
    } catch (error) {
      console.error('Error:', error);
      setError('Something went wrong. Please try again later.');
    } finally {
      setLoading(false); // Set loading to false after the fetch is done
    }
  };

  return (
    <div className="chat-box">
      <h2>Python Tutor</h2>
      <div id="messages">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`message ${msg.isBot ? 'bot' : ''}`}
          >
            {msg.text}
          </div>
        ))}
      </div>
      {error && <div className="error">{error}</div>} {/* Display error message */}
      <input
        type="text"
        id="user_input"
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
        placeholder="Ask a Python question..."
      />
      <button onClick={sendMessage} disabled={loading}>
        {loading ? 'Loading...' : 'Send'}
      </button>
    </div>
  );
}

export default App;
