import avatar from './images/boy.png';

import React, { useState } from 'react';
import './App.css';

function App() {
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [userInput, setUserInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);

  // Function to speak text using the SpeechSynthesis API
  const speakText = (text) => {
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.onstart = () => {
      setIsSpeaking(true); // Start speaking animation
    };
    utterance.onend = () => {
      setIsSpeaking(false); // Stop speaking animation after speech ends
    };
    speechSynthesis.speak(utterance);
  };

  const sendMessage = async () => {
    if (!userInput) return;

    // Add the user message to the chat
    setMessages(prevMessages => [
      ...prevMessages,
      { text: userInput, isBot: false },
    ]);
    setUserInput(''); // Clear input field
    setLoading(true); // Show loading indicator while waiting for the response

    // Simulate bot response
    setTimeout(() => {
      const botMessage = "This is a response from the bot.";

      // Add bot message to chat
      setMessages(prevMessages => [
        ...prevMessages,
        { text: botMessage, isBot: true },
      ]);

      // Speak the bot's response
      speakText(botMessage);

      setLoading(false); // Stop loading after response
    }, 2000); // Simulate a 2-second delay before the bot responds
  };

  return (
    <div className="chat-box">
      <h2>Python Tutor</h2>
      <div id="messages">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`message ${msg.isBot ? 'bot' : ''}`}
          >
            {msg.text}
          </div>
        ))}
      </div>
      <div className={`avatar ${isSpeaking ? 'speaking' : ''}`}>
        <img src={avatar} alt="Character" />
      </div>
      <input
        type="text"
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
        placeholder="Ask a Python question..."
      />
      <button onClick={sendMessage} disabled={loading}>
        {loading ? 'Loading...' : 'Send'}
      </button>
    </div>
  );
}

export default App;
