import React, { useRef, useEffect } from "react";

function Body({ messages, error, listening }) {
  const messagesEndRef = useRef(null);

  // Auto-scroll to latest message
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  return (
    <div className="container-screen">
    <div className="messages">
      {messages.map((msg, index) => (
        <div key={index} className={`message-wrapper ${msg.isBot ? 'bot-wrapper' : 'user-wrapper'}`}>
          <div className={`message ${msg.isBot ? 'bot' : 'user'}`}>{msg.text}</div>
        </div>
      ))}
      <div ref={messagesEndRef} />
    </div>

    {/* Fixed Footer Controls */}
    <div className="voice-controls">
      <button onClick={startListening} disabled={listening}>
        <FaMicrophone size={24} />
      </button>
      <button onClick={stopListening} disabled={!listening}>
        <FaStop size={24} />
      </button>
      <button className="nav-button" onClick={() => navigate("/")}>Go to Text Chat</button>
    </div>
  </div>

  );
}

export default Body;
