import React, { useState, useRef, useEffect } from 'react';
import './App.css';

function App() {
  const [userInput, setUserInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const audioRef = useRef(null); // To play speech

  const speakText = (text) => {
    // Check if SpeechSynthesis is supported
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      const voices = speechSynthesis.getVoices();

      // Set voice, pitch, and rate
      utterance.voice = voices[1]; // Set voice (optional)
      utterance.pitch = 1; // Normal pitch
      utterance.rate = 1; // Normal speaking rate

      // Speak the text
      speechSynthesis.speak(utterance);
    } else {
      console.error("Speech synthesis not supported in this browser.");
    }
  };

  const sendMessage = async () => {
    if (!userInput) return;

    setMessages(prevMessages => [
      ...prevMessages,
      { text: userInput, isBot: false },
    ]);
    setLoading(true);
    setError('');

    try {
      // Simulate bot response (You can replace it with actual API)
      const botMessage = "I'm your speakable character. How can I help you today?";

      // Add bot response to chat
      setMessages(prevMessages => [
        ...prevMessages,
        { text: botMessage, isBot: true },
      ]);

      // Make the character speak the bot's response
      speakText(botMessage);

      setUserInput('');
    } catch (error) {
      console.error('Error:', error);
      setError(`Something went wrong: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-box">
        <h2>Speakable Character</h2>

        {/* Character Avatar (use an image or emoji as a character) */}
        <div className="avatar">
          <img src="https://via.placeholder.com/100" alt="Character" />
        </div>

        <div className="messages">
          {messages.map((msg, index) => (
            <div key={index} className={`message ${msg.isBot ? 'bot' : 'user'}`}>
              <span className="message-text">{msg.text}</span>
            </div>
          ))}
        </div>

        {error && <div className="error">{error}</div>}

        <div className="input-container">
          <input
            type="text"
            id="user_input"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            placeholder="Ask me anything..."
            disabled={loading}
          />
          <button onClick={sendMessage} disabled={loading}>
            {loading ? 'Loading...' : 'Send'}
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
