import React, { useState } from 'react';
import './App.css';

function App() {
  const [userInput, setUserInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const sendMessage = async () => {
    if (!userInput) return;

    // Add the user message to the chat
    setMessages(prevMessages => [
      ...prevMessages,
      { text: userInput, isBot: false },
    ]);
    setLoading(true); // Set loading to true while waiting for the response
    setError(''); // Clear any previous error

    try {
      const response = await fetch(`http://127.0.0.1:8000/ai/res/?question=${encodeURIComponent(userInput)}`, {
        method: 'GET',
      });

      if (!response.ok) {
        throw new Error(`Server error: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();
      const botMessage = data.result;

      setMessages(prevMessages => [
        ...prevMessages,
        { text: botMessage, isBot: true },
      ]);
      setUserInput('');
    } catch (error) {
      console.error('Error:', error);
      setError(`Something went wrong: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-box">
        <h2>Python Tutor</h2>
        <div id="messages" className="messages">
          {messages.map((msg, index) => (
            <div key={index} className={`message ${msg.isBot ? 'bot' : 'user'}`}>
              {msg.text}
            </div>
          ))}
        </div>
        {error && <div className="error">{error}</div>}
        <div className="input-container">
          <input
            type="text"
            id="user_input"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            placeholder="Ask a Python question..."
            disabled={loading}
          />
          <button onClick={sendMessage} disabled={loading} className="send-button">
            {loading ? 'Loading...' : 'Send'}
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
